const Course = (course) => {
    console.log(course.course.name)
    const {name} = (course.course)

    const Header = (props) =>{
        console.log(props.name)
        return (
        <div>
            <h1>{props.name}</h1>
        </div>
        )
    }

    const Content = (props) => {
        console.log(props.parts)
        return (
            <div>
                {props.parts.map(part =>
                <Part key={part.name} course={part.name} ex={part.exercises} />
                )}
            </div>
        )
    }

    const Part = (props) => {
        return (
        <div>
            <p>{props.course} {props.ex}</p>
        </div>
        )
    }

    const Total = (props) => {
        const results = props.parts.map(part => part.exercises)
        console.log(results)
        let sum = 0;
        for (let i =0; i < results.length; i++) {
            sum += results[i];
        }
        console.log(sum)
        return (
        <div>
            <h2>total of {sum} exercises</h2>
        </div>
        )
    }

    return (
        
        <div>
            <Header name={name} />
            <Content parts ={course.course.parts} />
            <Total parts={course.course.parts} />
        </div>
    )

}


export default Course
